import { useState, useEffect } from "react"
import DeviceList from "./selectDevice"
import Modal from "../Components/modal"
import axios from "axios"
//import Driver from "../Selenium/driver"
const BOT = () => {
    const [text, setText] = useState('')
    const [post, setPost] = useState([])
    const [action, setAction] = useState();
    const [config, setConfig] = useState({})
    const [agent,setAgent] = useState([])
    const [selectedAgent,setSelectedAgent] = useState('')
    const [selectedDevices,setSelectedDevices] = useState([])
    //Process each lines of text-area
    function handleTextArea(event) {
        setText(event.target.value)
    }
    function combined() {
        const lines = text.split('\n').filter(line => line.trim());
        setPost(lines)
    }

    useEffect(() => {
        combined()
        // console.log(post)
    }, [text])

    /*
        1. Verify action chosen by user
        2. Render Form depends on action
            2.1 Read all the neccessory details like get the IP Address of devices and store in the variable / json
        3. Verify all input are filled
        4. Sent to server via Axios
        5. Return result

    */

    const handleActionChange = (event) => {
        let actionValue = event.target.value
        setAction(actionValue)
    }

    const handleAgentChange = (event) => {
        let thisAgent = event.target.value
        setSelectedAgent(thisAgent)
    }

    const handleSelectedDevices = (devices) => {
       setSelectedDevices(devices)

    };

    const execution = () =>{
        if(action === '1'){
            alert("Like POST")
        }
        if(action === '2'){
            alert("Like and Comment")
        }

        if(action === '3'){
            alert("Post Images")
        }

    }

    const likePost = () =>{
        selectedDevices.forEach((device)=>{
            let ip = device.device_ip
            likePostRequest(ip)
        })
    }

    const likePostRequest = async(ip)=>{
        let serverLink = 'http://'+ip+':5000/likePost'
        try{
            await axios.get(serverLink).then(response=>{
                if(response.data.status === 200){
                    alert(response.data.message)
                }
            })
        }
        catch(error){
            console.log(error)
        }
    }







    return (
        <main className="main" id="main">
            <div className="container-md">
                <Modal title="Choose Device(s)" modalID="selectDeviceModal" content={<DeviceList onSelectedDevicesChange={handleSelectedDevices} />} />
                {/* Device Select */}
                {/* <div className="card">
                    <div className="card-header">Select Device(s)</div>
                    <div className="card-body">
                        <DeviceList selected={handleSelectedDevices}/>
                    </div>
                </div> */}
                {/* End */}
                <div className="card">
                    <div className="card-header">Facebook BOT</div>
                    <br />
                    <div className="card-body">
                        <div className="config">
                            {/* 

                            1. Select How many PC to Run 
                            2. Select How many Account to Run at one time
                            3. Select How what action to run


                            */}
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#agencySelect" className="form-label">Select Device(s)</label>
                                        </div>
                                        <div className="col-8">
                                           <select name="agent" id="agencySelect" className="form-select" value={selectedAgent} onChange={handleAgentChange}>
                                            <option value="">Select Agency</option>
                                            <option value="agency1">Agency Johor</option>
                                           </select>

                                        </div>
                                    </div>
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#deviceSelect" className="form-label">Select Device(s)</label>
                                        </div>
                                        <div className="col-8">
                                          { selectedAgent?
                                             <button className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#selectDeviceModal">Choose Device(s)</button> : <p className="text-danger">Please Select Agency !</p>
                                          }
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#type" className="form-label">Action</label>
                                        </div>
                                        <div className="col-8">
                                            <select name="" id="" className="form-select" value={action} onChange={handleActionChange}>
                                                <option value="">Choose Action</option>
                                                <option value="1">Like Post</option>
                                                <option value="2">Like & Reply Comment</option>
                                                <option value="3">Reply Comment with Image</option>
                                            </select>
                                        </div>
                                    </div>


                                </div>
                            </div>
                            <br />
                            <div className="row">

                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#post" className="post-link">Post Link</label>
                                        </div>
                                        <div className="col-8">
                                            <textarea className="form-control" rows={10} columns={50} onChange={handleTextArea} value={text}></textarea>
                                        </div>
                                    </div>


                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="text-center">
                                    <button className="btn btn-primary" onClick={execution}>Settings</button>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
                <br /><br /><br />



            </div>
        </main>
    )
}

export default BOT;
