import {useState,useEffect} from "react"
import Modal from "../Components/modal";
import Form from "../Components/form";
import axios from "axios";
function Account(){
    const [accounts,setAccounts] = useState()

    const form = [

        {name:'facebook_id',type:"text",label:"Facebook ID",id:"facebook-id"},
        {name:"password",type:"password",label:"Password",id:"password"},
        {name: "otp", type: "text", label: "Encrypted Password", id: 'otp' },
        {
            name:'device',type:'select',label:'On Device',id:'device',
            options:[
                {value:'PC-1',label:'PC1'}
            ]
        },

    ]

    const handleData = (data) => {
        console.log(data)
    }

    useEffect(()=>{
        fetchAllAccounts();
    },[])

    const fetchAllAccounts = async() =>{
        try
        {
            const response = await axios.get('http://127.0.0.1:5000/allAccounts') 
            const {status,accounts} = response.data
            if(status === 2000){
                setAccounts(accounts)
            }
            else{
                console.log("Error to fetch account")
            }
        }catch(error){
            console.log(error)
        }
    }


    return (
        <main id="main" class="main">
        <div className="container-md">
            <Modal title="Add Facebook Account" content={<Form  formFields={form} onSubmit={handleData}/>} />
            <div className="card">
                <div className="card-header">Facebook Account</div>
                <div className="card-body">
                    <br />
                    <div className="text-end">
                        <button type="button" class="btn btn-outline-primary" data-bs-toggle="modal" data-bs-target="#modal">
                            <i className="bi bi-plus-circle"></i> Account
                        </button>

                    </div>
                    <br />
                    <br />

                    <table className="table table-sm table-borderless" id="instrument_table">
                        <thead className="table-dark">
                            <tr>
                                <th>No</th>
                                <th width="30%">Account</th>
                                <th width="30%">Run ON PC</th>
                                <th width="30%">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>1</td>
                                <td>Account 1</td>
                                <td>PC 2</td>



                                <td>
                                    <button className="btn btn-sm" title="Configure Instrument"><i className="bi bi-sliders"></i>&emsp;Edit</button>
                                    &emsp;
                                    <a href="/delete" title="Delete Instrument" className="btn btn-sm"><i className="bi bi-trash text-danger"></i>&emsp;Delete</a>
                                </td>
                            </tr>
                        </tbody>
                    </table>

                </div>
            </div>
        </div>
        </main>
    )
}

export default Account;
