import { useState,useEffect } from "react";
import Cookies from "js-cookie";
import Form from "../Components/form";
import Modal from "../Components/modal";
import loading from "../Components/loading";
import { getDevices } from "../Repositories/deviceRepos";
import {getAccountByDevice,createAcc,updateAcc,deleteAcc,retrieveAcc} from "../Repositories/accountRepos";
import Alert from "../Components/alert";
const Account = () =>{

    const [errors,setErrors] = useState([])
    const [message,setMessage] = useState("")
    const [isLoading,setisLoading] = useState(false)
    const [showAlert,setShowAlert] = useState(false)
    const agency = Cookies.get("agentID")
    const [accountsList,setAccountList] = useState([])
    const [account,setAccount] = useState()
    const [deviceList,setDeviceList] = useState([])
    const [selectedDevice,setSelectedDevice] = useState([])

    const Form = [
        {name:'agency',type:'text',label:'Agency',id:'agency', readOnly:true,value:{agency}},
        {name:'id',type:'text',label:'Facebook ID',id:'facebook-id'},
        {name:'password',type:'password',label:'password',id:'facebook-pass'},
        {name:'otp',type:'text',label:'One-Time Password',id:'otp'},
        {name:'device_id',type:'select',label:'Run On Device',id:'device_id'}


    ]
    const displayLoading = ()=>{
        setisLoading(true)
            setTimeout(() => {
                setisLoading(false)
            }, 2000);
    }
    useEffect(()=>{
        displayLoading()
        setAccountList([])
    },[selectedDevice])
    const fetchDevices = async()=>{
        try{
            let data = await getDevices(agency)
            if(data){
                setDeviceList(data)
            }
            else{
                setDeviceList([])
            }
        }catch(error){
            console.log(error)
        }
    }

    //Fetch All Devices in initial
    useEffect(()=>{
        fetchDevices()
    },[])

    const handleDeviceChange = (e) =>{
        let selected = e.target.value
        setSelectedDevice(selected)
    }

    const fetchAccountList = async() =>{
        try{
            let accounts = await getAccountByDevice(selectedDevice)
            if(accounts){
                setAccountList(accounts)
            }
            else{
                setAccountList([])
            }

        }catch(error){
        }
    }

    useEffect(()=>{
        fetchAccountList()
    },[selectedDevice])

    // CRUD

    const addAccount = async(accountData) =>{
        try{
            let newAccount = await createAcc(accountData)
            if(newAccount){
                setMessage("New Account has been created")
                setShowAlert(true)

            }
        }
        catch(error){}
    }


    return(
        <main className="main" id="main">
            <div className="container-md">
                {/** Modal Place Here */}
                <Modal title="Add Account" modalID="addAccountModal" content={<Form formFields={Form} onSubmit={addAccount} />} data="" />
                {/** End Modal Place Here */}
                <div className="card">
                    <div className="card-header">Account Management</div>
                    <div className="card-body">
                        <br />
                        {/**Alert */}
                            {showAlert && <Alert status="success" message={message} display="true"/>}
                        {/** EndAlert */}

                        <div className="text-end">
                            <button type="button" className="btn btn-outline-primary" data-bs-toggle="modal" data-bs-target="#addAccountModal">
                                <i className="bi bi-plus-circle"></i> Account
                            </button>
                        </div>
                        <br />
                        <br />
                        {/**filter */}
                            <div className="filter">
                                <div className="row">
                                    <div className="col-8">
                                        <div className="row">
                                            <div className="col-4">
                                                <label htmlFor="device" className="form-label">Select Device</label>
                                            </div>
                                            <div className="col-8">
                                                <select name="device" id="device" className="form-select" onChange={handleDeviceChange}>
                                                    <option value="">Please Select</option>
                                                    {
                                                        deviceList&&deviceList.map((device,index)=>(
                                                            <option value={device.device_id} key={index}>{device.device_name}</option>    
                                                        ))
                                                    }
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        {/** End filter */}
                        <br />
                        <br />

                        <table className="table table-borderless">
                            <thead className="table-dark">
                                <tr>
                                    <th>No.</th>
                                    <th>Account ID</th>
                                    <th>Action</th>
                                </tr>
                            </thead>

                            <tbody>
                                {
                                    isLoading?<tr><td colSpan="3">{loading}</td></tr>:
                                    accountsList.length > 0 ? 
                                        accountsList.map((account,index)=>(
                                            <tr key={index}>
                                                <td>{index +1}</td>
                                                <td>{account.id}</td>
                                                <td>
                                                        <button className="btn btn-sm" title="Edit Account" data-bs-toggle="modal" data-bs-target=""><i className="bi bi-sliders"></i>&emsp;Edit</button>
                                                        &emsp;
                                                        <button title="Delete Device" className="btn btn-sm" ><i className="bi bi-trash text-danger"></i>&emsp;Delete</button>
                                                    </td>
                                            </tr>    
                                        )):<tr><td colSpan="3"><p className="text-danger">No Accounts Found</p></td></tr>
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </main>    
    )
}

export default Account
