import { useState, useEffect } from "react";

const ImageComment = ({ accounts, posts }) => {

    const [image, setImage] = useState()

    useEffect(() => {
        console.log(accounts)
        console.log(posts)
    }, [accounts, posts])

    const pushImage = () => {
        if (accounts.length <= 0) {
            alert("Please Select Account")
        }
        if (posts.length <= 0) {
            alert("Please enter the post links")
        }
    }

    const handleImageChange = (e) => {
        setImage(e.target.files[0])
    }
    return (
        <div>
            <div className="row">
                <div className="col-8">

                    <div className="row">
                        <div className="col-2">
                            <label htmlFor="#image" className="form-label">Choose Image</label>
                        </div>
                        <div className="col-8">
                            <input type="file" name="image" id="image" className="form-control" />
                        </div>
                    </div>



                </div>
            </div>
            <br />
            <div className="row">
                <div className="col-8">
                    <div className="mb-3 justify-align-content-center">
                        <label className="form-label">Preview</label>
                        <div
                            className="border border-2 border-secondary d-flex align-items-center justify-content-center"
                            style={{ height: '500px', width: '500px' }}
                        >
                            {image && (
                                <img src={URL.createObjectURL(image)} alt="Preview" style={{ maxWidth: '100%', maxHeight: '100%' }} />
                            )}
                        </div>
                    </div>
                </div>
            </div>
            <br />
            <div className="text-center">
                <button className="btn btn-dark" onClick={pushImage}>Run BOT</button>
            </div>
            <br />

        </div>
    )

}
export default ImageComment
