            <div className="container-md">

                <Modal title="Add Facebook Account" modalID="addAccount" content={<Form formFields={form} onSubmit={addAccount} />} />
                {accountData && <Modal title="Edit" modalID="editAccount" content={<Form formFields={form} onSubmit={updateAccount} data={accountData}/>} />}

                {/* <Modal title="Edit" content={editForm}/> */}

                <div className="card">
                    <div className="card-header">Facebook Account</div>
                    <div className="card-body">
                        <br />
                        {isAlertShow && 
                            <div className="alertDiv">
                                <div className={errors.length != 0?'alert alert-danger':'alert alert-success'}>{message}</div>
                            </div>
                        }
                        <div className="text-end">
                          {selectedAgent === ""? '':<button type="button" className="btn btn-outline-primary" data-bs-toggle="modal" data-bs-target="#addAccount">
                                <i className="bi bi-plus-circle"></i> Account
                            </button>}

                        </div>
                        <br />
                        <br />
                          {/* Filter */}
                          {agents.length > 0 &&
                            <div className="filter" id="filter">
                                <div className="row">
                                    <div className="col-8">
                                        <div className="row">
                                            <div className="col-2">
                                                <label htmlFor="#type" className="form-label">Agency</label>
                                            </div>
                                            <div className="col-8">
                                                { agents ?
                                                <select name="type" id="type" className="form-select" onChange={handleAgencyChange}>
                                                    <option value="all">All</option>
                                                    {
                                                        agents.map((agent, index) => (
                                                            <option value={agent.id} key={index}>{agent.name}</option>
                                                        ))
                                                    }
                                                </select>:<p>Loading...</p>
                                                }
                                            </div>
                                        </div>


                                    </div>


                                    <div className="col-4">
                                        <div className="row text-end">
                                            {/* <button className="col-6 btn btn-outline-primary" onClick={filterDevice}><i className="bi bi-filter"></i> Filter</button> */}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        }
                        {/*End Filter */}
                        <br /><br />

                        <table className="table table-sm table-borderless" id="instrument_table">
                            <thead className="table-dark">
                                <tr>
                                    <th>No</th>
                                    <th width="30%">Account</th>

                                    <th width="30%">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {/* <tr>
                                    <td>1</td>
                                    <td>Account 1</td>
                                    <td>PC 2</td>



                                    <td>
                                        <button className="btn btn-sm" title="Configure Instrument"><i className="bi bi-sliders"></i>&emsp;Edit</button>
                                        &emsp;
                                        <a href="/delete" title="Delete Instrument" className="btn btn-sm"><i className="bi bi-trash text-danger"></i>&emsp;Delete</a>
                                    </td>


                                </tr> */}
                                <tr >
                                    <td colSpan="4">
                                   {
                                    load
                                   }
                                    </td>
                                </tr>


                                {
                                    accounts.map((account,index) => (

                                        <tr key={account.id}>
                                            <td>{index+1}</td>
                                            <td>{account.id}</td>

                                            <td>
                                            <button className="btn btn-sm" title="Edit Account" onClick={()=>getAccount(account.id)} data-bs-toggle="modal" data-bs-target="#editAccount"><i className="bi bi-sliders"></i>&emsp;Edit</button>
                                            &emsp;
                                            <button title="Delete Device" className="btn btn-sm" onClick={()=>deleteAccount(account.id)}><i className="bi bi-trash text-danger"></i>&emsp;Delete</button>
                                        </td>
                                        </tr>
                                    ))
                                }

                                {/* {

                                    accounts.map((acc,index)=>(
                                        <tr key={acc.id}>
                                            <td>{index++}</td>
                                            <td>{acc.id}</td>
                                            <td>{acc.password}</td>
                                        </tr>
                                    ))

                                } */}
                            </tbody>

                        </table>

                    </div>
                </div>
            </div>
        </main>
    )
}

export default Account;
