import { useState, useEffect } from "react"
//import Driver from "../Selenium/driver"
const BOT = () => {
    const [text, setText] = useState('')
    const [post, setPost] = useState([])

    //Process each lines of text-area
    function handleTextArea(event) {
        setText(event.target.value)


    }
    function combined() {
        const lines = text.split('\n').filter(line => line.trim());
        setPost(lines)
    }

    useEffect(() => {
        combined()
        // console.log(post)
    }, [text])

    // const test = async () => {
    //     const driver = new Driver(false); // Create an instance of the Driver class
    //     const url = 'https://www.example.com';
    //     const elementLocator = By.id('test');

    //     try {
    //       await driver.openURL(url); // Open the URL
    //       const element = await driver.getElement(elementLocator); // Get the element
    //       // Perform further actions on the element
    //       // ...

    //       await driver.click(elementLocator); // Click on the element
    //       // Click action completed

    //       driver.closeConnection(); // Close the connection
    //     } catch (error) {
    //       console.error('An error occurred:', error);
    //       driver.closeConnection(); // Close the connection in case of an error
    //     }
    //   };


    return (
        <main className="main" id="main">
            <div className="container-md">
                <div className="card">
                    <div className="card-header">Facebook BOT</div>
                    <div className="card-body">
                        <div className="config">

                            <div className="row">

                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#post" className="post-link">Post Link</label>
                                        </div>
                                        <div className="col-8">
                                            <textarea class="form-control" rows={10} columns={50} onChange={handleTextArea} value={text}></textarea>
                                        </div>
                                    </div>


                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#type" className="form-label">Action</label>
                                        </div>
                                        <div className="col-8">
                                            <select name="" id="" class="form-select">
                                                <option value="">Choose Action</option>
                                                <option value="1">Like Post</option>
                                                <option value="2">Like & Reply Comment</option>
                                                <option value="3">Like & Reply Comment with Image</option>
                                            </select>
                                        </div>
                                    </div>


                                </div>
                            </div>
                        </div>
                    </div>

                </div>
                <br /><br /><br />
                <button className="btn btn-dark">See</button>

            </div>
        </main>
    )
}

export default BOT;
