import { useState, useEffect } from "react";
import axios from "axios";
const LikePost = ({ accounts, posts }) => {
    const [data, setData] = useState({})

    const performLikeAction = async () => {

        if (data.length > 0) {
            try {
                await axios.post('http://127.0.0.1:5000/likePost', data).then(response => {
                    if (response.data.status === 200) {
                        alert("DONE")
                    }
                })
            }
            catch {

            }
        }
        else{
            alert("Please select at least one account or enter post link")
        }
    }

    useEffect(() => {
        setData({
            accounts: accounts,
            posts: posts
        });

    }, [accounts, posts])

    return (
        <div>
            <div className="row">
                <div className="col-8">

                </div>
                <div className="text-center">
                    <button className="btn btn-dark" onClick={performLikeAction}>RUN</button>
                </div>
            </div>
        </div>
    )
}

export default LikePost
