import {chrome} from "selenium-webdriver/chrome"

class Driver {
  // ...

  async getElement(element) {
    try {
      const target = await this.driver.wait(
        until.elementLocated(element),
        3000
      );
      return target;
    } catch (error) {
      console.log(`Element ${element} not found`);
    }
  }

  async click(element) {
    try {
      const target = await this.driver.wait(
        until.elementLocated(element),
        3000
      );
      await target.click();
    } catch (error) {
      console.log(`Time out for waiting ${element} to appear`);
    }
  }

  // ...
}
export default Driver

