import {collection, addDoc, getDocs,updateDoc,deleteDoc, getDoc,doc,set} from "firebase/firestore"
import database from "../firebase"

const agencyCollectionRef = collection(database,'Agency')

export const getAllAgents = async()=>{
    try{
        const data = await database.collection('Agency')
        const agent = data.docs.map((doc)=>({...doc.data(),id:doc.id}))
        return agent
    }catch(error){}
}

export const createAgency = async(data) =>{
    try{
        // const newAgent = await database.collection('Agency').doc(data.name)
        // return newAgent.setDoc(data)
        await set(doc(agencyCollectionRef,data.name),data)
    }catch(error){
        console.log(error)
    }
}
