import { collection, addDoc, getDocs, updateDoc, deleteDoc } from "firebase/firestore"
import database from "../firebase"

const AccountRepo = () => {
    const accountCollectionRef = collection(database, 'Accounts')
    const fetchAllAccounts = async () => {
        try {
            const data = await getDocs(accountCollectionRef);
            const account = data.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
            console.log(account)
            return account
        } catch (error) {
            console.log(error)
        }

    }
}

export default AccountRepo
