import { useState, useEffect } from "react";
import { getAllAgents } from "../Repositories/agencyRepos";
import Cookies from "js-cookie";
const Login = () => {
    const [agents,setAgents] = useState([])

    const [selectedAgent,setSelectedAgent] = useState('')

    const [errors,setErrors] = useState(false)

    const [message,setMessage] = useState('')

    const [name,setName] = useState('')

    const [auth,setAuth] = useState(false)

    const fetchAgents = async() =>{
        try{
            let data = await getAllAgents()
            if(data){
                setAgents(data)
            }
        }catch(error){
            setErrors(true)
            console.log(error)
        }
    }

    useEffect(()=>{
        fetchAgents()
    },[])

    const handleAgentChange = (e) =>{
        let value = e.target.value
        let thisID = document.getElementById(value).innerText

        if(value){
            setSelectedAgent(value)
            setName(thisID)
        }
        else{
            setSelectedAgent('')
            setName('')
        }

    }

    const authenticate = () =>{
        if(selectedAgent && name){
            Cookies.set('agentID',selectedAgent)
            Cookies.set('agentName',name)
            setAuth(true)
            setMessage("You are logged in")
            setTimeout(() => {
                window.location.href = "/bot"
            }, 2500);
        }
        const cookies = Cookies.get()
        console.log(cookies)


    }

    return (
        <main id="main" className="main">
            <div className="container-md">
                <div className="card">
                    <div className="card-body">
                        <br />
                        {
                            errors && <div className="alert alert-danger">{message}</div>
                        }

                        {
                            auth && <div className="alert alert-success">{message}</div>
                        }
                        <br />
                        <div className="row">
                            <div className="col-2"></div>
                            <div className="col-8">
                                <div className="row">
                                    <div className="col-4">
                                        <label htmlFor="#agent" className="form-label">Select Agency</label>
                                    </div>
                                    <div className="col-8">
                                        <select name="agent" id="agent" className="form-select" onChange={handleAgentChange}>
                                            <option value="">Please Select</option>
                                            {
                                                agents.length > 0 
                                                &&
                                                agents.map((agent,index)=>(
                                                    <option value={agent.id} key={index} id={agent.id}>{agent.name}</option>
                                                ))
                                            }
                                        </select>
                                    </div>
                                    <br /><br />

                                </div>

                                <div className="row">
                                    <div className="text-center">
                                        <button className="btn btn-primary" onClick={authenticate}>Login</button>
                                    </div>
                                </div>
                            </div>
                            <div className="col-2"></div>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    )
}

export default Login;
