import { useState, useEffect } from "react"
import axios from "axios"

const Comment = ({ data }) => {

    const sendComment = async()=>{
        try{
            await axios.post("http://192.168.0.133:5000").then(res=>{
                if(res.data.status === 200){
                    alert("OK")
                }
            })
        }catch(error){}
    }
    return (
        <div>
            <div className="row">
                <div className="col-8">

                    <div className="row">
                        <div className="col-2">
                            <label htmlFor="#tag" className="form-label">Reply To@</label>
                        </div>
                        <div className="col-8">
                            <input type="text" name="reply" id="reply" className="form-control" />
                        </div>
                    </div>
                    <br />

                    <div className="row">
                        <div className="col-2">
                            <label htmlFor="#comment" className="form-label">Comment:</label>
                        </div>
                        <div className="col-8">
                            <textarea name="message" id="message" cols="10" rows="10" className="form-control" />
                        </div>
                    </div>
                    <br />


                </div>
                <div className="text-center">
                    <button className="btn btn-dark">Comment</button>
                </div>
            </div>
        </div>
    )

}

export default Comment
