import { useState, useEffect } from "react"
import Cookies from "js-cookie"
import DeviceList from "./selectDevice"
import Modal from "../Components/modal"
import axios from "axios"
import { getAllAgents } from "../Repositories/agencyRepos"
import { getDevices } from "../Repositories/deviceRepos"
import AccountList from "./selectAccount"
import { getAccountByDevice } from "../Repositories/accountRepos"
import ImageComment from "./Actions/ImageComment"
import { render } from "@testing-library/react"

const BOT = () => {
    const [text, setText] = useState('')
    const [post, setPost] = useState([])
    const [action, setAction] = useState();
    const [config, setConfig] = useState({})
    const [agents,setAgents] = useState([])
    const [selectedAgent,setSelectedAgent] = useState(Cookies.get('agentID'))
    const agentName = Cookies.get("agentName")
    const [devices,setDevices] = useState([])
    const [selectedDevice,setSelectedDevice] = useState()
    const [selectedAccounts,setSelectedAccounts] = useState([])
    const [accounts,setAccounts] = useState([])

    const [renderForm,setRenderForm] = useState()
    //Process each lines of text-area
    // function handleTextArea(event) {
    //     setText(event.target.value)
    // }
    // function combined() {
    //     const lines = text.split('\n').filter(line => line.trim());
    //     setPost(lines)
    // }

    // useEffect(() => {
    //     combined()
    //     // console.log(post)
    // }, [text])

    /*
        1. Verify action chosen by user
        2. Render Form depends on action
            2.1 Read all the neccessory details like get the IP Address of devices and store in the variable / json
        3. Verify all input are filled
        4. Sent to server via Axios
        5. Return result

    */

    const fetchAgent = async() =>{
        let data = await getAllAgents()
        let agent = "AG16880452748148083"
        setAgents(data)
    }
    useEffect(()=>{
        fetchAgent()
    },[])
    useEffect(()=>{
        fetchDevices()
        setSelectedAccounts([])
        setSelectedDevice('')
    },[selectedAgent])

    const handleActionChange = (event) => {
        let actionValue = event.target.value
        setAction(actionValue)
    }

    const handleAgentChange = (event) => {
        let thisAgent = event.target.value
        setSelectedAgent(thisAgent)
    }

    const fetchDevices=async()=>{
        const this_devices = await getDevices(selectedAgent)
        setDevices(this_devices)
    }

    const handleSelectedDevices = (devices) => {
       setSelectedDevice(devices)
       console.log(devices)

    };

    const execution = () =>{
        if(action === '1'){
           setRenderForm()
        }
        if(action === '2'){
            alert("Like and Comment")
        }

        if(action === '3'){
            //alert("Post Images")
            setRenderForm(<ImageComment/>)
        }

    }



    const likePost = () =>{
        // selectedDevices.forEach((device)=>{
        //     let ip = device.device_ip
        //     likePostRequest(ip)
        // })
    }

    const likePostRequest = async(ip)=>{
        let serverLink = 'http://'+ip+':5000/likePost'
        try{
            await axios.get(serverLink).then(response=>{
                if(response.data.status === 200){
                    alert(response.data.message)
                }
            })
        }
        catch(error){
            console.log(error)
        }
    }

    const handleDeviceChange = (e)=>{
        let device = e.target.value
        setSelectedDevice(device)

    }

   const handleSelectedAccountChange=(account)=>{
        setSelectedAccounts(account)

    }

    const getAccounts = async()=>{

        const accountList = await getAccountByDevice(selectedDevice)
        if(accountList){
            setAccounts(accountList)
        }else{
            setAccounts([])
        }
    }

    useEffect(()=>{
        setAccounts([])
        getAccounts()
    },[selectedDevice,selectedAgent])

    const handleSelectAccount=(e)=>{
        let isChecked = e.target.checked
        let value = e.target.value
        if(isChecked){

            setSelectedAccounts(account=>[...account,value]);
            //console.log(selectedAccounts)
        }
        else{

            const accountList = selectedAccounts.filter((account)=>account!==value)
            setSelectedAccounts(accountList)
        }
    }



    return (
        <main className="main" id="main">
            <div className="container-md">
                <Modal title="Choose Account(s)" modalID="selectAccountModal" content={<AccountList device={selectedDevice} selectedAccountChangeHandling={handleSelectedAccountChange}/>} />

                {/* Device Select */}
                {/* <div className="card">
                    <div className="card-header">Select Device(s)</div>
                    <div className="card-body">
                        <DeviceList selected={handleSelectedDevices}/>
                    </div>
                </div> */}
                {/* End */}
                <div className="card">
                    <div className="card-header">Facebook BOT</div>
                    <br />
                    <div className="card-body">
                        <div className="config">
                            {/* 

                            1. Select How many PC to Run 
                            2. Select How many Account to Run at one time
                            3. Select How what action to run


                            */}
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            {/* <label htmlFor="#agencySelect" className="form-label">Agency : </label> */}
                                            <h5 className="fw-bold">{agentName}</h5>
                                        </div>
                                        <div className="col-8">

                                        </div>
                                        {/* <div className="col-8">
                                           <select name="agent" id="agencySelect" className="form-select" value={selectedAgent} onChange={handleAgentChange}>
                                                <option value="">Select Agency</option>
                                                {
                                                    agents.length > 0 &&
                                                    agents.map((agent,index)=>(
                                                        <option value={agent.id} key={agent.id}>{agent.name}</option>
                                                    ))
                                                }
                                           </select>

                                        </div> */}
                                    </div>
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#deviceSelect" className="form-label">Select Device(s)</label>
                                        </div>
                                        <div className="col-8">
                                          {/* { selectedAgent?
                                             <button className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#selectDeviceModal">Choose Device(s)</button> : <p className="text-danger">Please Select Agency !</p>
                                          } */}
                                          { selectedAgent?
                                            <select name="deviceOption" id="deviceSelect" className="form-select" onChange={handleDeviceChange}>
                                                <option value="">Select Devices</option>
                                                {
                                                    devices&&devices.map((device,index)=>(
                                                        <option value={device.device_id} key={index}>{device.device_name}</option>
                                                    ))
                                                }
                                            </select>:<p className="text-danger">Please Select Agency !</p>
                                          }
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#accountSelect" className="form-label">Select Account(s)</label>
                                        </div>
                                        <div className="col-8">
                                            <button className="btn btn-secondary" data-bs-toggle="modal" data-bs-target="#selectAccountModal">Choose Account(s)</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#type" className="form-label">Action</label>
                                        </div>
                                        <div className="col-8">
                                            <select name="" id="" className="form-select" value={action} onChange={handleActionChange}>
                                                <option value="">Choose Action</option>
                                                <option value="1">Like Post</option>
                                                <option value="2">Like & Reply Comment</option>
                                                <option value="3">Reply Comment with Image</option>
                                            </select>
                                        </div>
                                    </div>


                                </div>
                            </div>
                            <br />
                            <div className="row">

                                <div className="col-12">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#post" className="post-link">Post Link</label>
                                        </div>
                                        <div className="col-8">
                                            <textarea className="form-control" rows={10} columns={50} onChange={handleTextArea} value={text}></textarea>
                                        </div>
                                    </div>


                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="text-center">
                                    <button className="btn btn-primary" onClick={execution}>Settings</button>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
                <br /><br /><br />



            </div>
            <br />
            <div className="container-md">
                <div className="card">
                    <div className="card-header">
                        {agentName}
                    </div>
                    <div className="card-body">
                        <br />
                        {/**Filter Device */}
                        <div className="device-filter">
                            <div className="row">
                                <div className="col-8">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#device">Device</label>
                                        </div>
                                        <div className="col-8">
                                        { selectedAgent?
                                            <select name="deviceOption" id="deviceSelect" className="form-select" onChange={handleDeviceChange}>
                                                <option value="">Select Devices</option>
                                                {
                                                    devices&&devices.map((device,index)=>(
                                                        <option value={device.device_id} key={index}>{device.device_name}</option>
                                                    ))
                                                }
                                            </select>:<p className="text-danger">Please Select Agency !</p>
                                          }
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        {/** End Filter Device */}

                        <br />
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th>No.</th>
                                    <th>Account ID</th>
                                    <th>Action</th>
                                </tr>
                            </thead>

                            <tbody>
                                {
                                    accounts.length > 0 ?accounts.map((account,index)=>(
                                        <tr key={index+1}>

                                            <td>{index+1}</td>
                                            <td>{account.id}</td>
                                            <td>
                                                <input type="checkbox" id={account.id} value={account.id} className="form-input-check" onChange={handleSelectAccount}/>
                                            </td>
                                        </tr>    
                                    )):<tr><td colSpan="3">No Data </td></tr>
                                }
                            </tbody>


                        </table>
                        <br />
                        <hr />
                        <br />
                        <div className="row">
                                <div className="col-8">
                                    <div className="row">
                                        <div className="col-2">
                                            <label htmlFor="#type" className="form-label">Action</label>
                                        </div>
                                        <div className="col-8">
                                            <select name="" id="" className="form-select" value={action} onChange={handleActionChange}>
                                                <option value="">Choose Action</option>
                                                <option value="1">Like Post</option>
                                                <option value="2">Like & Reply Comment</option>
                                                <option value="3">Reply Comment with Image</option>
                                            </select>
                                        </div>
                                    </div>


                                </div>
                            </div>
                    </div>
                </div>
                <br />
                {renderForm}
            </div>
        </main>
    )
}

export default BOT;
