import { useState, useEffect } from "react"
import Modal from "../Components/modal";
import Form from "../Components/form";
import axios from "axios";
function Account() {
    const [accounts, setAccounts] = useState([]) 
    const [accountData,setAccountData] = useState({})
    const [devices,setDevices] = useState([])
    const [deviceOptions,setDeviceOptions] = useState([])
    const getAllDevices = async () => {
        try {
            await axios.get('http://127.0.0.1:5000/getAllDevices').then(response => {
                if (response.data.status === 200) {
                    const device = response.data.devices;
                    const devicesArray = Object.keys(device).map((key) => ({
                        name: key,
                        ...device[key],
                    }));
                    setDevices(devicesArray);

                }
            })
        }
        catch (error){

        }
    }

    useEffect(()=>{
        getAllDevices()
    },[])
    const form = [

        { name: 'id', type: "text", label: "Facebook ID", id: "facebook-id" },
        { name: "password", type: "password", label: "Password", id: "password" },
        { name: "otp", type: "text", label: "Encrypted Password", id: 'otp' },
        {
            name: 'device', type: 'select', label: 'On Device', id: 'device',
            options: devices.map((device)=>({
                value:device.name,
                label:device.name
            }))
        },

    ]






    const [editForm,setEditForm] = useState({})

    const [isAlertShow,setAlertShow] = useState(false)
    const [errors,setErrors] = useState([])
    const [message,setMessage] = useState()

    const fetchAllAccounts = async () => {
        try {
            await axios.get('http://127.0.0.1:5000/allAccounts').then(response => {
                console.log(response.data)
                if (response.data.status === 200) {
                    const accountsData = response.data.users;
                    const accountsArray = Object.keys(accountsData).map((key) => ({
                        id: key,
                        ...accountsData[key],
                    }));
                    setAccounts(accountsArray);
                }
            })

        } catch (error) {
            console.log(error)
        }
    }
    useEffect(() => {
        fetchAllAccounts();

    }, [])

    const addAccount = async(data) =>{
        //setAccountData(data)
        // const formData = Object.keys(data).map((key)=>({
        //     id:key,
        //     ...data[key],
        // }))
        // setAccountData(formData)
        // console.log(data)

        try{

            await axios.post('http://127.0.0.1:5000/addAccount',data).then(res=>{
                if(res.data.status === 200){
                    setMessage('New account added')
                    fetchAllAccounts()
                    setAlertShow(true)
                    setTimeout(()=>{
                        setAlertShow(false)
                    },3000)

                }
                else{
                   setMessage('Failed to add new account')

                }
            })
            //setAccountData({})
        }
        catch(error){
            console.log(error)
        }
    }

    const updateAccount = async(data) =>{
        //console.log(data)

        try{
            await axios.post('http://127.0.0.1:5000/updateAccount',data).then(response=>{
                if(response.data.status === 200){
                    setMessage('Account: '+data['id']+ ' has been updated')
                    setAlertShow(true)
                    setTimeout(()=>{
                        setAlertShow(false)
                    },3000)
                    window.location.reload()
                }
            })
        }catch(error){
            console.log(error)
        }
    }


    const deleteAccount = async(id) =>{
        try{
            await axios.delete('http://127.0.0.1:5000/deleteAccount/'+id).then(response=>{
                if(response.data.status === 200){
                    setMessage('Account : '+id+' has been deleted')
                    setAlertShow(true)
                    setTimeout(()=>{
                        setAlertShow(false)
                    },3000)
                    fetchAllAccounts()
                }
            })
        }catch(error){

            console.log(error)
        }
    }

    const getAccount = async(id)=>{
        try{
            await axios.get('http://127.0.0.1:5000/getAccount/'+id).then(response=>{
                if(response.data.status === 200){
                    let account = response.data.account
                    setAccountData(account)
                }
            })
        }
        catch(error){
            console.log(error)
        }

    }





    return (
        <main id="main" class="main">
            <div className="container-md">
                <Modal title="Add Facebook Account" modalID="addAccount" content={<Form formFields={form} onSubmit={addAccount} />} />
                {accountData &&<Modal title="Edit" modalID="editAccount" content={<Form formFields={form} onSubmit={updateAccount} data={accountData}/>} />}

                {/* <Modal title="Edit" content={editForm}/> */}

                <div className="card">
                    <div className="card-header">Facebook Account</div>
                    <div className="card-body">
                        <br />
                        {isAlertShow && 
                            <div className="alertDiv">
                                <div className={errors.length > 0?'alert alert-danger':'alert alert-success'}>{message}</div>
                            </div>
                        }
                        <div className="text-end">
                            <button type="button" class="btn btn-outline-primary" data-bs-toggle="modal" data-bs-target="#addAccount">
                                <i className="bi bi-plus-circle"></i> Account
                            </button>

                        </div>
                        <br />
                        <br />

                        <table className="table table-sm table-borderless" id="instrument_table">
                            <thead className="table-dark">
                                <tr>
                                    <th>No</th>
                                    <th width="30%">Account</th>
                                    <th width="30%">Run ON PC</th>
                                    <th width="30%">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {/* <tr>
                                    <td>1</td>
                                    <td>Account 1</td>
                                    <td>PC 2</td>



                                    <td>
                                        <button className="btn btn-sm" title="Configure Instrument"><i className="bi bi-sliders"></i>&emsp;Edit</button>
                                        &emsp;
                                        <a href="/delete" title="Delete Instrument" className="btn btn-sm"><i className="bi bi-trash text-danger"></i>&emsp;Delete</a>
                                    </td>


                                </tr> */}



                                {accounts.map((account,index) => (
                                    <tr key={account.id}>
                                        <td>{index+1}</td>
                                        <td>{account.id}</td>
                                        <td>{account.device}</td>
                                        <td>
                                        <button className="btn btn-sm" title="Edit Account" onClick={()=>getAccount(account.id)} data-bs-toggle="modal" data-bs-target="#editAccount"><i className="bi bi-sliders"></i>&emsp;Edit</button>
                                        &emsp;
                                        <button title="Delete Device" className="btn btn-sm" onClick={()=>deleteAccount(account.id)}><i className="bi bi-trash text-danger"></i>&emsp;Delete</button>
                                    </td>
                                    </tr>
                                ))}

                                {/* {

                                    accounts.map((acc,index)=>(
                                        <tr key={acc.id}>
                                            <td>{index++}</td>
                                            <td>{acc.id}</td>
                                            <td>{acc.password}</td>
                                        </tr>
                                    ))

                                } */}
                            </tbody>
                        </table>

                    </div>
                </div>
            </div>
        </main>
    )
}

export default Account;
