import {useState,useEffect} from "react"
import axios from "axios"

const DeviceList = () =>{
    const [devices,setDevices] = useState([])

    const fetchDevices = async()=>{
        try{
            await axios.get('http://127.0.0.1:5000/getAllDevices').then(response => {
                if(response.data.status === 200){
                    const device = response.data.devices;
                    const devicesArray = Object.keys(device).map((key) => ({
                        name: key,
                        ...device[key],
                    }));
                    setDevices(devicesArray);
                }
            })
        }
        catch(error){
            console.log(error)
        }

    }

    useEffect(()=>{
        fetchDevices()
    },[])

    const [selectedDevice,setSelectedDevice] = useState([])

    const handleSelect = (deviceName) => (event) => {
        const isChecked = event.target.checked;

        setSelectedDevices((prevSelectedDevices) => {
          if (isChecked) {
            // Add the selected device to the selectedDevice state
            return [...prevSelectedDevices, deviceName];
          } else {
            // Remove the unchecked device from the selectedDevice state
            return prevSelectedDevices.filter((device) => device !== deviceName);
          }
        });
      };


    return(
        <div className="device-list">
            <div className="row">
                <div className="col-12">
                    <table className="table table-borderless">
                        <thead>
                           <tr>
                            <th>Check</th>
                            <th>PC</th>
                           </tr>
                        </thead>

                        <tbody>
                            {
                                devices.map((device,index)=>(
                                    <tr>
                                        <td>
                                            <input className="form-check-input device-checkbox" type="checkbox" onChange={handleSelect(device.device_name)} />
                                        </td>
                                        <th>
                                            {device.device_name}
                                        </th>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>    
    )
}

export default DeviceList
