import { useState,useEffect } from "react";
import Cookies from "js-cookie";
import Form from "../Components/form";
import Modal from "../Components/modal";
import { getDevices } from "../Repositories/deviceRepos";
import {getAccountByDevice} from "../Repositories/accountRepos";
const Account = () =>{
    const [errors,setErrors] = useState([])
    const [message,setMessage] = useState("")

    const agency = Cookies.get("agencyID")
    const [accountsList,setAccountList] = useState([])
    const [account,setAccount] = useState()
    const [deviceList,setDeviceList] = useState([])
    const [selectedDevice,setSelectedDevice] = useState([])

    const Form = []

    const fetchDevices = async()=>{
        try{
            let data = await getDevices(agency)
            if(data){
                setDeviceList(data)
            }
        }catch(error){
            console.log(error)
        }
    }

    //Fetch All Devices in initial
    useEffect(()=>{
        fetchDevices()
    },[agency])

    const handleDeviceChange = (e) =>{
        let selected = e.target.value
        setSelectedDevice(selected)
    }

    const fetchAccountList = async() =>{
        try{
            let accounts = await getAccountByDevice(selectedDevice)
            if(accounts){
                setAccountList(accounts)
            }
        }catch(error){
        }
    }

    useEffect(()=>{
        fetchAccountList()
    },[selectedDevice])


    return(
        <main className="main" id="main">
            <div className="container-md">
                {/** Modal Place Here */}

                {/** End Modal Place Here */}
                <div className="card">
                    <div className="card-header">Account Management</div>
                    <div className="card-body">
                        <br />
                        {/**Alert */}

                        {/** EndAlert */}

                        <div className="text-end">
                            <button type="button" className="btn btn-outline-primary" data-bs-toggle="modal" data-bs-target="">
                                <i className="bi bi-plus-circle"></i> Account
                            </button>
                        </div>
                        <br />
                        <br />
                        {/**filter */}
                            <div className="filter">
                                <div className="row">
                                    <div className="col-8">
                                        <div className="row">
                                            <div className="col-2">
                                                <label htmlFor="device" className="form-label">Select Device</label>
                                            </div>
                                            <div className="col-8">
                                                <select name="device" id="device" className="form-select" onChange={handleDeviceChange}>
                                                    <option value="">Please Select</option>
                                                    {
                                                        deviceList.map((device,index)=>(
                                                            <option value={device.device_id} key={index}>{device.device_name}</option>    
                                                        ))
                                                    }
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        {/** End filter */}
                    </div>
                </div>
            </div>
        </main>    
    )
}

export default Account
