import { useState,useEffect } from "react";
import axios from "axios";
const LikePost = ({accounts,posts}) =>{
    const[data,setData] = useState({})

    const performLikeAction = async() =>{

        if(data){
            try{
                await axios.post('http://127.0.0.1:5000/likePost',data).then(response=>{
                    if(response.data.status === 200){
                        alert("DONE")
                    }
                })
            }
            catch{

            }
        }
    }

    useEffect(()=>{
        setData({
            accounts:accounts,
            posts:posts
        });

    },[accounts,posts])

    return(
        <div>
            <div className="row">
                <div className="col-8">
                    <div className="row">
                        <div className="col-2">
                            <label htmlFor="#post" className="form-label">Post Link(s)</label>
                        </div>
                        <div className="col-8">
                            <textarea name="" id="post" cols="30" rows="10" className="form-control"></textarea>
                        </div>
                    </div>

                    <button className="btn btn-dark" onClick={performLikeAction}>RUN</button>
                </div>
            </div>
        </div>    
    )
}

export default LikePost
