import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
const Form = ({ formFields, onSubmit,data }) => {
    const [formData, setFormData] = useState({})
    const [valid, setValid] = useState(false)
    const [errorMsg, setErrorMsg] = useState()

    const [value,setValue] = useState()

    useEffect(()=>{
        if(data){
            setFormData(data)
        }
    },[data])
   
    const handleInputChange = (event) => {
        const { name, value } = event.target
        
        setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    };

    const validation = () => {
        const errors = []
        formFields.forEach((field) => {
            if (formData[field.name] === undefined || formData[field.name] === "") {
                errors.push(<li>[ {field.label} ] is required</li>)

                //Add each error into an array and print below 
                //make use of useEffect

            }

        });
        return errors

    }

    const formSubmit = (event) => {
        event.preventDefault()
        const errors = validation()
        setValid(errors.length === 0)
        setErrorMsg(errors)

        if (errors.length === 0) {
            onSubmit(formData)
            console.log(formData)
            document.getElementById("closeModal").click()
            event.target.reset()
            setFormData({})
        }

    }



    return (
        <div className="row">
            <div className="col-12">
                <div className={valid ? 'collapse' : ''}>
                    <ul className="text-danger">
                        {
                            errorMsg
                        }
                    </ul>
                </div>
                <form onSubmit={formSubmit} autoComplete="off">
                    {
                        formFields.map((field) =>
                            <div className="mb-3">
                                <label htmlFor={field.id} className="form-label">{field.label}</label>
                                {
                                    // field.type === 'input'?
                                    // (<input type={field.type} classname="form-control" id={field.id}/>):
                                    field.type === 'select' ?
                                        (<select className="form-select" name={field.name} onChange={handleInputChange} value={formData[field.name] || ''} >
                                            <option value="">Please Select</option>
                                            {field.options.map((option) =>
                                                <option key={option.value} value={option.value}  selected={option.value === formData[field.name]}>{option.label}</option>
                                            )}
                                        </select>) :
                                        (<input type={field.type} name={field.name} className="form-control" id={field.id} value={formData?formData[field.name] : ''}  onChange={handleInputChange} />)

                                }
                            </div>

                        )
                    }
                    <div className="text-center">
                        <button className="btn btn-dark">Submit</button>
                    </div>

                </form>
            </div>
        </div>
    )
}

export default Form
