import { useState, useEffect } from "react"
import Form from "../Components/form"
import Modal from "../Components/modal"
import axios from "axios"
const Device = () => {
    const formData = [
        { name: "device_name", type: "text", label: "Device Name", id: 'device_name' },
        { name: 'device_ip', type: "text", label: "Device's IP", id: 'device_ip' }
    ];

    const [devices, setDevices] = useState([])
    const [deviceDetail,setDeviceDetail] = useState()
    const [isAlertShow, setAlertShow] = useState(false)
    const [errors, setErrors] = useState([])
    const [message, setMessage] = useState()

    useEffect(()=>{
        getAllDevices()
    },[])
    const addDevice = async (data) => {
        try {
            await axios.post('http://127.0.0.1:5000/addDevice', data).then(response => {
                if (response.data.status === 200) {
                    setMessage("Device added !")
                    getAllDevices()
                    setAlertShow(true)
                    setTimeout(() => {
                        setAlertShow(false)
                    }, 3000);

                }
            })
        } catch (error) {
            console.log(error)
        }
    }



    const getAllDevices = async () => {
        try {
            await axios.get('http://127.0.0.1:5000/getAllDevices').then(response => {
                if (response.data.status === 200) {
                    const device = response.data.devices;
                    const devicesArray = Object.keys(device).map((key) => ({
                        name: key,
                        ...device[key],
                    }));
                    setDevices(devicesArray);
                }
            })
        }
        catch (error) {

        }
    }

    const getDevice = async(id) =>{
        try{
            await axios.get('http://127.0.0.1:5000/getDevice/'+id).then(response=>{
                if(response.data.status === 200){
                    let device = response.data.device
                    setDeviceDetail(device)

                }
            })
        }catch(error){

        }

    }

    const updateDevice = async(data)=>{
        let device_id = data['device_name']

        try{
            await axios.post('http://127.0.0.1:5000/updateDevice/'+device_id,data).then(response=>{
                if(response.data.status === 200){
                    setMessage('Device updated')
                    getAllDevices()
                    setAlertShow(true)
                    setTimeout(() => {
                        setAlertShow(false)
                    }, 3000);

                }
            })
        }
        catch(error){

        }

    }

    const deleteDevice = async(id)=>{
        try{
            await axios.delete('http://127.0.0.1:5000/deleteDevice'+id).then(response=>{
                if(response.data.status === 200){
                    setMessage('Device : '+id+' has been removed')
                    setAlertShow(true)
                    setTimeout(() => {
                        setAlertShow(false)

                    }, 3000);
                    getAllDevices()
                }
            })
        }
        catch(error){

        }
    }




    return (
        <main id="main" className="main">
            <div className="container-md">
                <Modal title="Add Device" modalID="addDeviceModal" content={<Form formFields={formData} onSubmit={addDevice} />} />
                <Modal title="Edit Device" modalID="editDeviceModal" content={<Form formFields={formData} onSubmit={updateDevice} data={deviceDetail} />} />
                <div className="card">
                    <div className="card-header">Device Management</div>
                    <div className="card-body">
                        <br />
                        {isAlertShow && 
                            <div className="alertDiv">
                                <div className={errors.length > 0?'alert alert-danger':'alert alert-success'}>{message}</div>
                            </div>
                        }
                        <div className="text-end">
                            <button type="button" class="btn btn-outline-primary" data-bs-toggle="modal" data-bs-target="#addDeviceModal">
                                <i className="bi bi-plus-circle"></i> Device
                            </button>

                        </div>
                        <br />
                        <br />

                        <table className="table table-sm table-borderless" id="instrument_table">
                            <thead className="table-dark">
                                <tr>
                                    <th>No</th>
                                    <th width="30%">Devices</th>
                                    <th width="30%">IP Address</th>
                                    <th >Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {/* <tr>
                                <td>1</td>

                                <td>PC 1</td> 

                                <td>192.168.1.133</td>


                                <td>
                                    <button className="btn btn-sm" title="Configure Instrument"><i className="bi bi-sliders"></i> &emsp;Edit</button>
                                    &emsp; &emsp;
                                    <a href="/delete" title="Delete Instrument" className="btn btn-sm"><i className="bi bi-trash text-danger"></i> &emsp;Delete</a>
                                </td>
                            </tr> */}

                                {devices.map((device, index) => (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>{device.name}</td>
                                        <td>{device.device_ip}</td>
                                        <td>
                                             <button className="btn btn-sm" title="Edit Device" onClick={() =>getDevice(device.name)} data-bs-toggle="modal" data-bs-target="#editDeviceModal"><i className="bi bi-sliders"></i>&emsp;Edit</button>
                                            &emsp;
                                            <button title="Delete Device" className="btn btn-sm" onClick={() =>deleteDevice(device.name)}><i className="bi bi-trash text-danger"></i>&emsp;Delete</button> 
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>

                    </div>
                </div>
            </div>
        </main>
    )
}

export default Device
